let burgerBtn=document.querySelector(".header__menu-burger"),closeBtn=document.querySelector(".menu-mobile__close");if(burgerBtn){let e=document.querySelector(".menu-mobile");burgerBtn.onclick=function(){e.classList.add("__active")}}if(closeBtn){let e=document.querySelector(".menu-mobile");closeBtn.onclick=function(){e.classList.remove("__active")}}window.onscroll=function(){let e=document.querySelector(".header");window.pageYOffset>0?e.classList.add("__active"):e.classList.remove("__active")};