function startTimer(t,e,n,o){var r,c,s=t;setInterval((function(){r=parseInt(s/60,10),c=parseInt(s%60,10),r=r<10?"0"+r:r,c=c<10?"0"+c:c,e.textContent=r,o.textContent=c,--s<0&&(s=n)}),1e3)}window.onload=function(){startTimer(1800,document.querySelector("#minutes"),60,document.querySelector("#seconds"))};